import React, {useEffect, useState} from 'react';
import API from '../api';

export default function Courses({token, onLogout}){
  const [courses, setCourses] = useState([]);
  const [form, setForm] = useState({code:'', title:'', credits:3, description:''});

  const load = async () => {
    const data = await API.listCourses(token);
    setCourses(Array.isArray(data) ? data : []);
  };

  useEffect(()=>{ load(); },[]);

  const create = async e => {
    e.preventDefault();
    await API.createCourse(token, form);
    setForm({code:'', title:'', credits:3, description:''});
    load();
  };

  const del = async id => {
    await API.deleteCourse(token, id);
    load();
  };

  return (
    <div style={{padding:20}}>
      <div style={{display:'flex', justifyContent:'space-between'}}>
        <h2>Courses</h2>
        <button onClick={()=>{ API.logout(token); onLogout(); }}>Logout</button>
      </div>

      <form onSubmit={create} style={{marginBottom:20}}>
        <input placeholder="Code" value={form.code} onChange={e=>setForm({...form, code:e.target.value})} required />
        <input placeholder="Title" value={form.title} onChange={e=>setForm({...form, title:e.target.value})} required />
        <input type="number" placeholder="Credits" value={form.credits} onChange={e=>setForm({...form, credits:parseInt(e.target.value||0)})} />
        <input placeholder="Description" value={form.description} onChange={e=>setForm({...form, description:e.target.value})} />
        <button type="submit">Create</button>
      </form>

      <table border="1" cellPadding="8">
        <thead><tr><th>ID</th><th>Code</th><th>Title</th><th>Credits</th><th>Actions</th></tr></thead>
        <tbody>
        {courses.map(c=> (
          <tr key={c.id}>
            <td>{c.id}</td>
            <td>{c.code}</td>
            <td>{c.title}</td>
            <td>{c.credits}</td>
            <td><button onClick={()=>del(c.id)}>Delete</button></td>
          </tr>
        ))}
        </tbody>
      </table>
    </div>
  );
}
